import React, { useState, useEffect } from 'react'
import { Container, Button, Row, Col } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import Workout from "../components/Workout"
import { LinkContainer } from 'react-router-bootstrap'
import { listWorkouts } from '../actions/workoutActions'
import Loader from "../components/Loader"
import Message from "../components/Message"


const CalandarScreen = () => {
    const dispatch = useDispatch()

    const [date, setDate] = useState(new Date("2021-10-02T11:00:00"))

    const workoutList = useSelector((state) => state.workoutList)
    const { loading, error, workouts } = workoutList

    const [workoutsOfDay, setWorkoutsOfDay] = useState([])
    

    useEffect(() => {
       dispatch(listWorkouts())
       setWorkoutsOfDay(workouts.filter(w => w.date === date)) 
    }, [dispatch])

    return (
        <Container className='mt-5'>
            <Row>
                <Col md={6}>
                    <div>
                        Here will be the Calander
                    </div>
                </Col>
                <Col md={6}>
                    {loading && <Loader />}
                    {error && <Message variant="danger">{error}</Message>}

                        {workouts && workoutsOfDay.map(workout => (
                            <Workout workout={workout} />
                        ))}
                </Col>
            </Row>
        </Container>
            
    )
}

export default CalandarScreen
