import React, { useState, useEffect } from 'react'
import { Container, Card } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import Workout from "../components/Workout"
import { listWorkouts } from '../actions/workoutActions'

const HomeScreen = () => {

    const dispatch = useDispatch()
    
    const [logged, setLogged] = useState(true)

    const listWorkouts = useSelector(state => state.listWorkouts)
    const { loading, workouts } = listWorkouts


    useEffect(() => {
       dispatch(listWorkouts())
    }, [dispatch])


    return (
        (
            <>
            <Container className='mt-5'>
                <h1 className='mb-5'>All Available Workouts</h1>
                {workouts.map(workout => (
                    <Workout workout={workout} />
                ))}
            </Container>
            </>
    ))
                }


export default HomeScreen
